package main

import "fmt"

func main() {

	floor := 0

	basement := 0

	for i, x := range getInput() {
		switch x {
		case '(':
			floor += 1
		case ')':
			floor -= 1
		}

		if floor == -1 && basement == 0 {
			basement = i + 1
		}
	}

	fmt.Println(floor, basement)
}

